mapper.select:
SELECT NVL(MAX(ROWNUM), 0)
FROM order_
WHERE email= #{email }

SELECT NVL(MAX(ROWNUM), 0)
FROM order_
WHERE num= #{num }

SELECT menu, menuCount, price
FROM order_
WHERE orderNum= #{orderNum }

mapper.update:
UPDATE order_
SET cancel=#{cancel } ,confirm=#{confirm }
WHERE orderNum=#{orderNum }
