mapper.update:
update store
set storeTag=#{storeTag}
where num=#{num}

update store
set storeName=#{storeName}, storeAddr=#{storeAddr}, openingTime=#{openingTime}
where num=#{num}

update store
set imageCheck=#{imageCheck}
where num=#{num}

update store


		set image_logo=#{image_logo}


		set image_1=#{image_1}


		set image_2=#{image_2}


		set image_3=#{image_3}


		set image_4=#{image_4}


where num=#{num} and owner=#{owner}

update store
set storeOpen=#{storeOpen}
where num=#{num}
