mapper.select:
select * from (select result1.*, rownum as rnum from (select * from store where storeOpen='yes'
and (lower(storeAddr) like '%' || lower(#{area}) || '%' or lower(storeName) like '%' || lower(#{area}) || '%' or lower(storeTag) like '%' || lower(#{area}) || '%') and (lower(storeAddr) like '%' || lower(#{keyword}) || '%' or lower(storeName) like '%' || lower(#{keyword}) || '%' or lower(storeTag) like '%' || lower(#{keyword}) || '%')
and (lower(storeAddr) like '%' || lower(#{area}) || '%' or lower(storeName) like '%' || lower(#{area}) || '%' or lower(storeTag) like '%' || lower(#{area}) || '%')
and (lower(storeAddr) like '%' || lower(#{keyword}) || '%' or lower(storeName) like '%' || lower(#{keyword}) || '%' or lower(storeTag) like '%' || lower(#{keyword}) || '%')
) result1) where rnum between #{startRowNum} and #{endRowNum}

mapper.update:
update store
set storeTag=#{storeTag}
where num=#{num}

update store
set storeName=#{storeName}, storeAddr=#{storeAddr}, storePhone=#{storePhone}, openingTime=#{openingTime}
where num=#{num}

update store


		set image_logo=#{image_logo}


		set image_1=#{image_1}


		set image_2=#{image_2}


		set image_3=#{image_3}


		set image_4=#{image_4}


where num=#{num} and owner=#{owner}

update store
set storeOpen=#{storeOpen}
where num=#{num}
